  border-right-style: none;
  border-right-width: 2px;
  border-right-color: #D3D3D3;
}

#tqvqulhvns .gt_sourcenote {
  font-size: 90%;
  padding: 4px;
}

#tqvqulhvns .gt_left {
  text-align: left;
}

#tqvqulhvns .gt_center {
  text-align: center;
}

#tqvqulhvns .gt_right {
  text-align: right;
  font-variant-numeric: tabular-nums;
}

#tqvqulhvns .gt_font_normal {
  font-weight: normal;
}

#tqvqulhvns .gt_font_bold {
  font-weight: bold;
}

#tqvqulhvns .gt_font_italic {
